/* eslint-env node */
/* eslint max-params:[0,10] */
/* eslint fecs-camelcase:[2,{"ignore":["/opt_/"]}] */

var util = require('util');

var u = require('underscore');
var debug = require('debug')('bce-sdk:BccClient');

var BceBaseClient = require('./bce_base_client');


/**
 * BCC service api
 *
 * 内网API地址：http://api.bcc.bce-sandbox.baidu.com
 * 沙盒API地址：http://bcc.bce-api.baidu.com
 *
 * @see http://gollum.baidu.com/BceDocumentation/BccOpenAPI#简介
 *
 * @constructor
 * @param {Object} config The bcc client configuration.
 * @extends {BceBaseClient}
 */
function BcdClient(config) {
    BceBaseClient.call(this, config, 'bcd', true);
}
util.inherits(BcdClient, BceBaseClient);

// --- BEGIN ---

// POST /instance
BcdClient.prototype.domainResolveList = function (data) {
  // {
  //   "domain" : domain,
  //   "pageNo" : pageNo,
  //   "pageSize" : pageSize
  // }
  return this.sendRequest('POST', '/v1/domain/resolve/list', {
      // config: options.config,
      // params: params,
      body: JSON.stringify(data)
  });
};

// POST /v{version}/domain/resolve/edit HTTP/1.1
// Host: bcd.baidubce.com
// Authorization: authorization string
// {
//   "domain" : domain,
//   "rdType" : rdType,
//   "view" : view,
//   "rdata" : rdata,
//   "ttl" : ttl,
//   "zoneName" : zoneName,
//   "recordId" : recordId
// }
BcdClient.prototype.domainResolveEdit = function (data) {
  // {
  //   "domain" : domain,
  //   "pageNo" : pageNo,
  //   "pageSize" : pageSize
  // }
  return this.sendRequest('POST', '/v1/domain/resolve/edit', {
      // config: options.config,
      // params: params,
      body: JSON.stringify(data)
  });
};
// --- E N D ---


module.exports = BcdClient;
